@import url("https://fonts.googleapis.com/css2?family=Red+Hat+Mono&display=swap");
*,
*::before,
*::after {
  margin: 0;
  padding: 0;
  box-sizing: border-box; }

* {
  scrollbar-width: thin;
  scrollbar-color: #4d4ae8 rgba(0, 0, 0, 0.347); }

*::-webkit-scrollbar {
  width: 8px; }

*::-webkit-scrollbar-track {
  background: rgba(0, 0, 0, 0.347); }

*::-webkit-scrollbar-thumb {
  background-color: #4d4ae8;
  border-radius: 20px;
  border: 3px solid rgba(0, 0, 0, 0.347); }

body {
  display: flex;
  height: 100vh;
  justify-content: center;
  width: 100%;
  background-color: #b4535348; }

.chat {
  max-width: 1100px;
  margin: auto;
  padding: 0 1rem;
  text-align: center; }
  .chat__btn {
    position: fixed;
    right: 10px;
    bottom: 10px;
    width: 50px;
    height: 50px;
    border: 1px solid #4d4ae8;
    border-radius: 50%;
    box-sizing: border-box;
    background-color: #4d4ae8;
    box-shadow: 0 10px 15px rgba(0, 0, 0, 0.784);
    opacity: 0.7;
    transition: opacity 0.5s;
    cursor: pointer;
    color: #fff;
    font-size: 1rem; }
    .chat__btn:hover {
      opacity: 1; }
    .chat__btn:active {
      transform: translateY(3px); }
  .chat__container {
    display: none;
    position: fixed;
    bottom: 60px;
    right: 60px;
    flex-direction: column;
    justify-content: space-between;
    height: 400px;
    width: 300px;
    padding: 10px;
    border-radius: 10px;
    box-shadow: 0 10px 15px rgba(0, 0, 0, 0.784);
    background-color: #a49d9d; }
  .chat .active {
    display: flex; }
  .chat__window {
    height: 80%;
    overflow-y: auto; }
  .chat__message {
    position: relative;
    display: flex;
    align-items: center;
    margin: 5px;
    padding: 5px;
    border-radius: 10px;
    background-color: #0872fe;
    color: #fff;
    font-size: 1rem; }
  .chat__bot {
    width: 60%;
    margin: 10px 0; }
  .chat__user {
    width: 60%;
    margin: 5px 0 5px auto; }
  .chat__input {
    position: relative;
    display: flex;
    justify-content: space-between; }
    .chat__input--field {
      width: 88%;
