@import url("https://fonts.googleapis.com/css2?family=Red+Hat+Mono&display=swap");
*,
*::before,
*::after {
  margin: 0;
  padding: 0;
  box-sizing: border-box; }

body {
  background-color: #b4535348;
  display: flex;
  justify-content: center;
  height: 100vh;
  width: 100%; }

.chat {
  max-width: 1100px;
  margin: auto;
  text-align: center;
  padding: 0 1rem; }
  .chat__btn {
    position: fixed;
    right: 10px;
    bottom: 10px;
    width: 50px;
    height: 50px;
    cursor: pointer;
    background-color: #4d4ae8;
    border: 1px solid #4d4ae8;
    border-radius: 50%;
    box-shadow: 0 10px 15px rgba(0, 0, 0, 0.784);
    box-sizing: border-box;
    color: #fff;
    font-size: 1rem;
    opacity: 0.7;
    transition: opacity 0.5s; }
    .chat__btn:hover {
      opacity: 1; }
    .chat__btn:active {
      transform: translateY(3px); }
  .chat__container {
    display: none;
    position: fixed;
    bottom: 60px;
    right: 60px;
    height: 400px;
    width: 300px;
    background-color: #a49d9d;
    flex-direction: column;
    justify-content: space-between;
    padding: 10px;
    border-radius: 10px;
    box-shadow: 0 10px 15px rgba(0, 0, 0, 0.784); }
  .chat .active {
    display: flex; }
  .chat__window {
    height: 80%;
    overflow-y: auto; }
  .chat__message {
    display: flex;
    align-items: center;
    background-color: #0872fe;
    color: #fff;
    padding: 5px;
    border-radius: 10px;
    font-size: 1rem;
    margin: 5px;
    position: relative; }
  .chat__bot {
    width: 60%;
    margin: 10px 0; }
  .chat__user {
    width: 60%;
    margin: 5px 0 5px auto; }
  .chat__input {
    position: relative;
    display: flex;
    justify-content: center; }
    .chat__input--field {
      width: 100%;
      border: none;
      border-radius: 5px;
      background-color: rgba(255, 255, 255, 0.654);
      font-size: 1rem;
      padding: 5px; }
      .chat__input--field:focus {
        background-color: #fff; }
    .chat__input--submit {
      font-size: 1rem;
      padding: 5px;
      border: none;
      border-radius: 50%;
      margin-left: 5px;
      color: #4d4ae8;
      cursor: pointer; }
      .chat__input--submit:active {
        transform: translateY(3px); }
  .chat__ques {
    padding: 10px; }
    .chat__ques-btn {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      border-radius: 10px;
      padding: 5px;
      margin: 10px auto;
      font-size: 1rem;
      cursor: pointer;
      background-color: #4d4ae8;
      border: 1px solid #4d4ae8; }

.btnQue {
  cursor: pointer; }
  .btnQue:hover {
    color: #800101;
    font-size: 15px; }

.loader span {
  width: 10px;
  height: 10px;
  margin: 0 5px;
  background-color: rgba(0, 0, 0, 0.202);
  border-radius: 50%;
  display: inline-block;
  animation-name: dots;
  animation-duration: 1.7s;
  animation-iteration-count: infinite;
  animation-timing-function: ease-in-out; }
  .loader span:nth-child(2) {
    animation-delay: .4s; }
  .loader span:nth-child(3) {
    animation-delay: .8s; }

@keyframes dots {
  50% {
    opacity: 0;
    transform: scale(0.7) translateY(10px);
    background-color: #c8bdbd; } }
